import pickle
# Import all the things we need ---
#   by setting env variables before Keras import you can set up which backend and which GPU it uses

import os
import numpy as np
import matplotlib.pyplot as plt
#import seaborn as sns


    
Xd = pickle.load(open('2016.04C.multisnr.pkl','rb'), encoding='iso-8859-1')
snrs,mods = map(lambda j: sorted(list(set(map(lambda x: x[j], Xd.keys())))), [1,0])
#在j=0，1的情况分别计算，并形成List
print(mods)
print(snrs)
X = [] #输入的sample 2*128的list
lbl = [] #需要分类的label(mod,snr)
for mod in mods:
    for snr in snrs:
        X.append(Xd[(mod,snr)])
        for i in range(Xd[(mod,snr)].shape[0]):  lbl.append((mod,snr))
X = np.vstack(X)

print(X[0])
print(lbl[0])

np.random.seed(2016)
n_examples = X.shape[0]
n_train = n_examples//2
train_idx = np.random.choice(range(0,n_examples), size=n_train, replace=False)
test_idx = list(set(range(0,n_examples))-set(train_idx))

print(len(train_idx))
print(len(test_idx))

X_train = X[train_idx]#还是输入样本的形式
X_test =  X[test_idx]

def to_onehot(yy):
    yy1 = np.zeros([len(yy), max(yy)+1])
    yy1[np.arange(len(yy)),yy] = 1
    return yy1
Y_train = to_onehot(list(map(lambda x: mods.index(lbl[x][0]), train_idx)))
Y_test = to_onehot(list(map(lambda x: mods.index(lbl[x][0]), test_idx)))#转换成独热向量编码

